  background: rgba(0, 0, 0, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-left: none;
  border-radius: 0 ${({ theme }) => theme.borderRadiusSm} ${({ theme }) => theme.borderRadiusSm} 0;
  padding: 0.75rem 1rem;
  color: ${({ theme }) => theme.textLight};
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:focus {
    outline: none;
    border-color: ${({ theme }) => theme.primary};
    box-shadow: 0 0 0 2px rgba(79, 172, 254, 0.2);
  }
  
  option {
    background: ${({ theme }) => theme.backgroundDark};
  }
`;

const RadioGroup = styled.div`
  display: flex;
  gap: 1rem;
  
  @media (max-width: ${({ theme }) => theme.breakpoints.sm}) {
    flex-direction: column;
    gap: 0.5rem;
  }
`;

const RadioLabel = styled.label`
  flex: 1;
  cursor: pointer;
`;

const RadioInput = styled.input`
  display: none;
  
  &:checked + div {
    background: rgba(79, 172, 254, 0.15);
    border-color: ${({ theme }) => theme.primary};
    
    svg {
      color: ${({ theme }) => theme.primaryLight};
    }
  }
`;

const RadioButton = styled.div`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem 1rem;
  background: rgba(0, 0, 0, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: ${({ theme }) => theme.borderRadiusSm};
  transition: all 0.3s ease;
  
  &:hover {
    background: rgba(0, 0, 0, 0.3);
  }
`;

const RadioIcon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${({ theme }) => theme.textMuted};
  font-size: 1.2rem;
  transition: color 0.3s ease;
`;

const CalculateButton = styled(motion.button)`
  background: linear-gradient(90deg, ${({ theme }) => theme.primaryLight}, ${({ theme }) => theme.primary});
  color: ${({ theme }) => theme.textDark};
  border: none;
  padding: 0.75rem 1rem;
  border-radius: ${({ theme }) => theme.borderRadiusSm};
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.15s ease, box-shadow 0.15s ease;
  box-shadow: 0 6px 18px rgba(79, 172, 254, 0.12);
  
  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;

const ResultContainer = styled(motion.div)`
  display: flex;
  gap: 1rem;
  align-items: center;
  background: ${({ theme }) => theme.backgroundLight};
  padding: 1rem;
  border-radius: ${({ theme }) => theme.borderRadiusSm};
  border: 1px solid ${({ theme }) => theme.cardBorder};
`;

const ResultIcon = styled.div`
  font-size: 2rem;
  color: ${({ theme }) => theme.primary};
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ResultText = styled.div``;

const ResultTitle = styled.h3`
  margin: 0;
  font-size: 1rem;
  color: ${({ theme }) => theme.textLight};
`;

const ResultValue = styled.p`
  margin: 0.25rem 0 0;
  font-size: 1.25rem;
  font-weight: 700;
`;

const ResultDetails = styled.p`
  margin: 0.25rem 0 0;
  color: ${({ theme }) => theme.textMuted};
  font-size: 0.9rem;
`;

const InfoBox = styled.div`
  margin-top: 1rem;
  display: flex;
  gap: 0.5rem;
  align-items: flex-start;
  color: ${({ theme }) => theme.textMuted};
`;

export default TimeCalculator;
